import math
class Solution(object):
    def zeroFilledSubarray(self, nums):
        check_1 = True if len(nums) >=1 and len(nums) < pow(10,5) else False
        check_2 = any(list(1 for i in range(0,len(nums)) if nums[i] < -pow(10,9) or nums[i] > pow(10,9)))
        solutions_glob = 0
        nums_copy = list(abs(nums[x]) for x in range(0,len(nums)))
        if check_1 and not check_2:
            step = 1
            while True:
                solutions = 0
                i = 0
                while i < len(nums_copy):
                    if sum(nums_copy[i:i+step]) == 0 and len(nums_copy[i:i+step]) == step:
                        solutions = solutions + 1
                    i = i+1
                if not solutions:
                    break
                step += 1
                solutions_glob += solutions
        return solutions_glob

